        public ValueTask Process(Tweet tweet)
        {
            TwitterStats.IncrementTweet();

            var text = tweet.Data?.Text;
            if (text != null)
            {
                var hasEmoji = false;
                foreach (var emoji in emojiUnicodes)
                {
                    if (text.Contains(emoji))
                    {
                        hasEmoji = true;
                        TwitterStats.AddEmoji(emoji);
                    }
                    if (hasEmoji)
                    {
                        TwitterStats.IncrementEmojiTweet();
                    }
                }
            }

            return new ValueTask();
        }
    }
